mapper.select:
SELECT count(ch.id) as count FROM cif_history ch
AND upper(ch.device_code) like concat('%', concat(upper(#{deviceCode}), '%'))
and ch.auth_status=#{authStatus} and ch.branch_id=#{branchId} and ch.updated_by!=#{updatedBy}

SELECT * FROM( SELECT q.*, rownum as seqNo FROM( select ch.id as id, ch.cif_id as cifId, ch.device_code as deviceCode, ch.cif_name as cifName, ch.host_cif_id as hostCifId, ch.blast_sms_time as blastSmsTime, ch.blast_sms as blastSms, ch.branch_id as branchId, ch.created_on as createOn, ch.created_by as createBy, ch.updated_on as updatedOn, ch.updated_by as updatedBy, CASE ch.cif_id WHEN 0 THEN 'Register' ELSE 'Update' END as activityType FROM cif_history ch
AND upper(ch.device_code) like concat('%', concat(upper(#{deviceCode}), '%'))
AND ch.auth_status=#{authStatus} and ch.updated_by!=#{updatedBy} and ch.branch_id=#{branchId}
ORDER BY ${sortVariable} ${sortOrder} ) q WHERE rownum <= #{rowEnd}) WHERE seqNo >= #{rowStart}
